document.addEventListener('DOMContentLoaded',()=>{if(typeof EnlighterJS!=='undefined'&&EnlighterJS!==null){EnlighterJS.init('synhi, precustom, nrcode','synhi, codecustom',{theme:'classic',indent:4})}});function darkThemeChange(){var xhr=new XMLHttpRequest();xhr.open('POST','/local/darkTheme.php');xhr.send({DarkStatus:"change"});var darkSwitch=document.getElementById("upel-dark-input");if(darkSwitch.checked){document.documentElement.classList.remove('upel-dark-theme')}else{document.documentElement.classList.add('upel-dark-theme')}}
function sendEmail(){event.preventDefault();var form=document.getElementById("signUpForm");const params=new FormData(form);var xhr=new XMLHttpRequest();xhr.onreadystatechange=function(){if(xhr.readyState===4){var res=JSON.parse(xhr.responseText);console.log(res);if(res.status=="success"){document.getElementById("cDescription").value='';document.getElementById("footer-popover").click();alert("Dziękujemy. Zgłoszenie zostało wysłane.")}else{alert("Nie można wysłąć Twojego zgłoszenia. Napisz na upel@agh.edu.pl, albo spróbuj później.")}}}
xhr.open('POST','/theme/upelceliid/layout/send.php');xhr.send(params)}